import React, { useEffect, useState } from 'react';

function App() {
  const [quiz, setQuiz] = useState(null);

  useEffect(() => {
    fetch('https://quiz-backend-uoqh.onrender.com/get_quiz?id=20250630_120448')
      .then((res) => res.json())
      .then((data) => setQuiz(data));
  }, []);

  if (!quiz) return <div>Načítání kvízu...</div>;

  return (
    <div>
      <h1>{quiz.title}</h1>
      {quiz.questions.map((q, idx) => (
        <div key={idx}>
          <p><strong>{q.question}</strong></p>
          <ul>
            {q.options.map((opt, i) => (
              <li key={i}>{opt}</li>
            ))}
          </ul>
        </div>
      ))}
    </div>
  );
}

export default App;
